mapper.select:

SELECT * FROM (
	SELECT ROW_NUMBER() OVER() as rownum, A.* FROM (SELECT *
FROM coupon ORDER BY COUPON_NAME DESC)A)AS foo WHERE rownum BETWEEN #{start} AND #{end}

mapper.update:

UPDATE coupon
