	SELECT ROW_NUMBER() OVER() as rownum, A.* FROM (SELECT *
FROM seller WHERE SELLER_GOODS_YN = true ORDER BY STORE_NAME ASC)A)AS foo WHERE rownum BETWEEN #{start} AND #{end}


